theme.customCss:
@font-face {
  font-family: 'witchesAwesome';
  src:  url('/static/themes/witches/witchesAwesome.eot');
  src:  url('/static/themes/witches/witchesAwesome.eot') format('embedded-opentype'),
    url('/static/themes/witches/witchesAwesome.ttf') format('truetype'),
    url('/static/themes/witches/witchesAwesome.woff') format('woff'),
    url('/static/themes/witches/witchesAwesome.svg') format('svg');
  font-weight: normal;
  font-style: normal;
}

@font-face {
  font-family: 'FontAwesomeSolid';
  font-style: normal;
  font-weight: 900;
  font-display: block;
  src: url("https://cdn.bootcdn.net/ajax/libs/font-awesome/5.15.2/webfonts/fa-solid-900.eot");
  src: url("https://cdn.bootcdn.net/ajax/libs/font-awesome/5.15.2/webfonts/fa-solid-900.eot?#iefix") format("embedded-opentype"), url("https://cdn.bootcdn.net/ajax/libs/font-awesome/5.15.2/webfonts/fa-solid-900.woff2") format("woff2"), url("https://cdn.bootcdn.net/ajax/libs/font-awesome/5.15.2/webfonts/fa-solid-900.woff") format("woff"), url("https://cdn.bootcdn.net/ajax/libs/font-awesome/5.15.2/webfonts/fa-solid-900.ttf") format("truetype"), url("https://cdn.bootcdn.net/ajax/libs/font-awesome/5.15.2/webfonts/fa-solid-900.svg#fontawesome") format("svg"); }

.NavPanel a[href="/main/friends"]::before {
    content: "\f015";
    font-family: witchesAwesome;
    position: absolute;
    color: var(--icon,#666);
    font-size: large;
    width: 1em;
    text-align: center;
}

.NavPanel a[href="/main/friends"] svg {
    opacity: 0;
}

.NavPanel a[href$="/interactions"]::before {
    content: "\f0f3";
    font-family: witchesAwesome;
    position: absolute;
    color: var(--icon,#666);
    font-size: large;
    width: 1em;
    text-align: center;
}

.NavPanel a[href$="/interactions"] svg {
    opacity: 0;
}

.NavPanel a[href$="/chats"]::before {
    content: "\f086";
    font-family: FontAwesomeSolid;
    position: absolute;
    color: var(--icon,#666);
    font-size: large;
    width: 1em;
    text-align: center;
}

.NavPanel a[href$="/chats"] svg {
    opacity: 0;
}

.NavPanel a[href="/about"]::before {
    content: "\f0eb";
    font-family: FontAwesomeSolid;
    position: absolute;
    color: var(--icon,#666);
    font-size: large;
    width: 1em;
    text-align: center;
}

.NavPanel a[href="/about"] svg {
    opacity: 0;
}

.ReplyButton button::before {
    content: "\f112";
    font-family: witchesAwesome;
    position: absolute;
    color: var(--icon,#666);
    font-size: larger;
    width: 1em;
    text-align: center;
}

.ReplyButton button:hover::before, .ReplyButton button.-active::before {
    color: var(--cBlue,#0095ff);
}

.ReplyButton button svg {
    opacity: 0;
}

.RetweetButton button::before {
    content: "\f079";
    font-family: witchesAwesome;
    position: absolute;
    color: var(--icon,#666);
    font-size: larger;
    width: 1em;
    text-align: center;
}

.RetweetButton button:hover::before, .RetweetButton button.-repeated::before {
    color: var(--cGreen,#0fa00f);
}

.RetweetButton button svg {
    opacity: 0;
}

.FavoriteButton button::before {
    content: "\f005";
    font-family: witchesAwesome;
    position: absolute;
    color: var(--icon,#666);
    font-size: larger;
    width: 1em;
    text-align: center;
}

.FavoriteButton button:hover::before, .FavoriteButton button.-favorited::before {
    color: var(--cOrange,orange);
}

.FavoriteButton button svg {
    opacity: 0;
}

span.ReactButton:before {
    content: "\f118";
    font-family: FontAwesomeSolid;
    position: absolute;
    color: var(--icon,#666);
    font-size: larger;
    width: 1em;
    text-align: center;
}

span.ReactButton:hover::before {
    color: var(--text,#b9b9ba);
}

span.ReactButton svg {
    opacity: 0;
}

.TimelineMenu a[href="/main/all"]:before {
    content: "\f0ac";
    font-family: witchesAwesome;
    position: absolute;
    color: var(--icon,#666);
    font-size: large;
    width: 1.4em;
    text-align: center;
}

.TimelineMenu a[href="/main/all"] svg {
    opacity: 0;
}

.ScopeSelector button:last-child::before {
    content: "\f0ac";
    font-family: witchesAwesome;
    position: absolute;
    color: var(--icon,#666);
    font-size: larger;
    width: 1.4em;
    text-align: center;
}

.ScopeSelector button.selected:last-child::before {
    color: var(--lightText,#b9b9ba);
}

.ScopeSelector button:last-child svg {
    opacity: 0;
}
